import React from 'react'
import './Layout.css'
import './Docs.css'
import 'wow.js/css/libs/animate.css'
import {Helmet} from 'react-helmet'
import styled from 'styled-components'
import Scrollspy from 'react-scrollspy'
import appPackage from '../package.json'

const LayoutContainer = styled.div`
  display: flex;
  display: -webkit-box;
  display: -ms-flexbox;
  display: -webkit-flex;
  flex-direction: column;
  max-width: 800px;
  margin: 0 auto;
  padding: 0;
`

const Nav = styled.div`
  z-index: 999;
  position: sticky;
  top: 0;
  left: 0;
  right: 0;
  display: flex;
  display: -webkit-box;
  display: -ms-flexbox;
  display: -webkit-flex;
  justify-content: space-between;
  margin: 0;
  padding: ${props => !props.hasScrolled
      ? '10px 0 10px 0'
      : '0'};
  background: ${props => props.hasScrolled
    ? 'rgba(238, 238, 238, 0.9)'
    : "#422c46 url('data:image/png;base64,') repeat"};
  transition: all 0.4s ease-in-out;
  box-shadow: ${props => !props.hasScrolled
    ? 'none'
    : '0 1px 0px rgba(0, 0, 0, 0.15)'};

  & a {
    color: ${props => props.hasScrolled
      ? 'black'
      : 'white'
    };
  }

  & img {
    filter: ${props => props.hasScrolled
      ? 'invert()'
      : 'none'
    };
  }
`

const NavItems = styled(Scrollspy)`
  list-style-type: none;
  display: inline-flex;
  margin: 0;
  margin-top: 12px;
`

const NavItem = styled.li`
  display: inline;
  text-transform: uppercase;

  &.isActive {
    a {
      color: #d800ff;
    }
  }

  &.isTabActive {
    a {
      color: #d800ff;
    }
  }
`

const NavLink = styled.a`
  text-decoration: none;
  margin: 12px;
  margin-top: 0px;
  text-align: left;
  vertical-align: middle;
  display: inline-block;
  font-size: 12px;
  :hover {
    color: #e4a5f0;
  }
`

const NavLogoImage = styled.img`
  height: 45px;
  width: 45px;
  margin: 6px;
`

const NavLogoLink = styled.a`
  margin: 0px;
  text-decoration: none;
  display: flex;
  display: -webkit-box;
  display: -ms-flexbox;
  display: -webkit-flex;
`

const NavLogoBrand = styled.div`
  font-weight: 400;
  text-transform: uppercase;
  font-size: 24px;
  margin-top: 12px;
`

const Content = styled.main`
  width: 100%;
  background-color: white;
`

const Footer = styled.footer`
  position: absolute;
  right: 0;
  bottom: 0;
  left: 0;
  display: flex;
  display: -webkit-box;
  display: -ms-flexbox;
  display: -webkit-flex;
  flex-direction: column;
  max-width: 800px;
  margin: 0 auto;
  padding: 0;
  color: #eee;
  font-size: 10px;
  background: #422c46 url('data:image/png;base64,') repeat;
`

export default class Layout extends React.Component {
  state = {
    hasScrolled: false,
  }

  componentDidMount () {
    const WOW = require('wow.js')
    new WOW().init({offset: 100, mobile: true});
    window.onscroll = () => this.setState({hasScrolled: window.scrollY > 20})
  }

  render() {
    return (
      <LayoutContainer>
        <Helmet
          htmlAttributes={{lang: 'en'}}
        >
          <title>DropStack</title>
          <meta charset="utf-8" />
          <meta http-equiv="X-UA-Compatible" content="IE=edge" />
          <meta name="viewport" content="width=device-width, initial-scale=1" />
          <meta name="theme-color" content="#eee" />
          <link href="https://fonts.googleapis.com/css?family=Source+Sans+Pro:400,300,400italic,600,200,600italic,700,900" rel="stylesheet" />
          <link href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.6.3/css/font-awesome.min.css" rel="stylesheet" />
        </Helmet>
        <Nav hasScrolled={this.state.hasScrolled}>
          <NavLogoLink href="/"><NavLogoImage src="/icon-dropstack.svg" alt="icon dropstack logo" /><NavLogoBrand>DropStack</NavLogoBrand></NavLogoLink>
          <NavItems items={ ['home', 'about'] } currentClassName="isActive">
            <NavItem><NavLink href={'/#home'}>Home</NavLink></NavItem>
            <NavItem><NavLink href={'/#about'}>About</NavLink></NavItem>
            <NavItem className={this.props.router.location.pathname.includes('/docs') ? 'isTabActive' : ''}><NavLink href={'/docs'}>Docs</NavLink></NavItem>
            <NavItem><NavLink href={'https://dashboard.cloud.dropstack.run/login'} target="_blank" rel="noopener">Login</NavLink></NavItem>
          </NavItems>
        </Nav>
        <Content>
          {this.props.children}
        </Content>
        <Footer>
          <NavItems>
            <NavItem><NavLink href="https://codecommission.com" target="_blank" rel="noopener">©2017 dropstack.run · Code Commission UG · All right reserved</NavLink></NavItem>
            <NavItem><NavLink href="https://www.codecommission.com/legal.html" target="_blank" rel="noopener">Legal</NavLink></NavItem>
            <NavItem>{appPackage.name} {appPackage.version}</NavItem>
          </NavItems>
        </Footer>
      </LayoutContainer>
    )
  }
}